import { useState } from 'react';
import axios from 'axios';

function App() {
  const [file, setFile] = useState(null);
  const [uploadStatus, setUploadStatus] = useState('');
  const [requirements, setRequirements] = useState(null);

  const handleUpload = async (e) => {
    e.preventDefault();
    if (!file) {
      setUploadStatus('Please select a file first');
      return;
    }

    const formData = new FormData();
    formData.append('srsDocument', file);

    try {
      setUploadStatus('Uploading and processing...');
      const res = await axios.post('/api/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });
      setRequirements(res.data.requirements);
      setUploadStatus('Document processed successfully!');
    } catch (err) {
      setUploadStatus('Processing failed: ' + err.message);
      setRequirements(null);
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 p-8">
      <h1 className="text-3xl font-bold text-blue-600 mb-6">QA Automation System</h1>

      <div className="bg-white p-6 rounded-lg shadow-md max-w-2xl mx-auto">
        <h2 className="text-xl font-semibold mb-4">Upload SRS Document</h2>

        <form onSubmit={handleUpload} className="space-y-4">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">
              Select SRS Document (PDF/DOCX)
            </label>
            <input
              type="file"
              onChange={(e) => setFile(e.target.files[0])}
              className="block w-full text-sm text-gray-500
                file:mr-4 file:py-2 file:px-4
                file:rounded-md file:border-0
                file:text-sm file:font-semibold
                file:bg-blue-50 file:text-blue-700
                hover:file:bg-blue-100"
              accept=".pdf,.docx"
            />
          </div>

          <button
            type="submit"
            className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500"
          >
            Upload Document
          </button>

          {uploadStatus && (
            <p className={`mt-2 text-sm ${
              uploadStatus.includes('Success') ? 'text-green-600' : 'text-red-600'
            }`}>
              {uploadStatus}
            </p>
          )}
        </form>

        {requirements && (
          <div className="mt-6 bg-white p-4 rounded-lg shadow-md">
            <h3 className="text-lg font-semibold mb-2">Extracted Requirements</h3>
            <div className="bg-gray-50 p-3 rounded overflow-auto max-h-60">
              <pre className="text-sm whitespace-pre-wrap">
                {requirements.rawText.substring(0, 1000)}
                {requirements.rawText.length > 1000 && '...'}
              </pre>
            </div>
            <p className="text-xs text-gray-500 mt-1">
              Showing first 1000 characters of {requirements.rawText.length} total
            </p>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
