import { useState } from 'react';
import axios from 'axios';

function App() {
  const [file, setFile] = useState(null);
  const [uploadStatus, setUploadStatus] = useState('');
  const [requirements, setRequirements] = useState(null);
  const [isLoading, setIsLoading] = useState(false);

  const handleUpload = async (e) => {
    e.preventDefault();
    if (!file) {
      setUploadStatus('Please select a file first');
      return;
    }

    const formData = new FormData();
    formData.append('srsDocument', file);

    try {
      setIsLoading(true);
      setUploadStatus('Uploading and analyzing document...');
      setRequirements(null);

      const res = await axios.post('/api/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      setRequirements(res.data.requirements);
      setUploadStatus('Analysis complete!');
    } catch (err) {
      setUploadStatus(`Error: ${err.response?.data?.error || err.message}`);
      console.error('Upload failed:', err);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 p-4 md:p-8">
      <div className="max-w-6xl mx-auto">
        <h1 className="text-2xl md:text-3xl font-bold text-blue-600 mb-6">QA Automation System</h1>

        <div className="bg-white p-4 md:p-6 rounded-lg shadow-md mb-6">
          <h2 className="text-lg md:text-xl font-semibold mb-3">Upload SRS Document</h2>

          <form onSubmit={handleUpload} className="space-y-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Select Document (PDF/DOCX)
              </label>
              <input
                type="file"
                onChange={(e) => {
                  setFile(e.target.files[0]);
                  setRequirements(null);
                }}
                className="block w-full text-sm text-gray-500
                  file:mr-4 file:py-2 file:px-4
                  file:rounded-md file:border-0
                  file:text-sm file:font-semibold
                  file:bg-blue-50 file:text-blue-700
                  hover:file:bg-blue-100"
                accept=".pdf,.docx"
                disabled={isLoading}
              />
            </div>

            <button
              type="submit"
              className={`px-4 py-2 rounded-md text-white ${isLoading ? 'bg-gray-400' : 'bg-blue-600 hover:bg-blue-700'} focus:outline-none focus:ring-2 focus:ring-blue-500`}
              disabled={isLoading}
            >
              {isLoading ? 'Processing...' : 'Analyze Document'}
            </button>

            {uploadStatus && (
              <p className={`text-sm ${uploadStatus.includes('Error') ? 'text-red-600' : 'text-green-600'}`}>
                {uploadStatus}
              </p>
            )}
          </form>
        </div>

        {requirements && (
          <div className="space-y-6">
            {/* Extracted Text Section */}
            <div className="bg-white p-4 md:p-6 rounded-lg shadow-md">
              <h3 className="text-lg font-semibold mb-3">Document Content</h3>
              <div className="bg-gray-50 p-3 rounded overflow-auto max-h-60">
                <pre className="text-sm whitespace-pre-wrap font-sans">
                  {requirements.rawText.length > 1500 
                    ? `${requirements.rawText.substring(0, 1500)}...` 
                    : requirements.rawText}
                </pre>
              </div>
              <p className="text-xs text-gray-500 mt-2">
                Showing {Math.min(requirements.rawText.length, 1500)} of {requirements.rawText.length} characters
              </p>
            </div>

            {/* Requirements Classification */}
            <div className="bg-white p-4 md:p-6 rounded-lg shadow-md">
              <h3 className="text-lg font-semibold mb-3">Identified Requirements</h3>
              <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-3">
                {requirements.features.labels.map((label, index) => (
                  <div key={index} className="bg-blue-50 p-3 rounded border border-blue-100">
                    <p className="font-medium capitalize">{label.replace('-', ' ')}</p>
                    <div className="mt-1">
                      <div className="w-full bg-gray-200 rounded-full h-2">
                        <div 
                          className="bg-blue-600 h-2 rounded-full" 
                          style={{ width: `${(requirements.features.scores[index] * 100).toFixed(0)}%` }}
                        ></div>
                      </div>
                      <p className="text-xs text-gray-600 mt-1">
                        Confidence: {(requirements.features.scores[index] * 100).toFixed(1)}%
                      </p>
                    </div>
                  </div>
                ))}
              </div>
            </div>

            {/* Key Entities */}
            {requirements.entities && requirements.entities.length > 0 && (
              <div className="bg-white p-4 md:p-6 rounded-lg shadow-md">
                <h3 className="text-lg font-semibold mb-3">Key Entities Found</h3>
                <div className="flex flex-wrap gap-2">
                  {requirements.entities.map((entity, index) => (
                    <div 
                      key={index} 
                      className="bg-green-50 px-3 py-1 rounded-full text-sm border border-green-100 flex items-center"
                    >
                      <span className="font-medium">{entity.word}</span>
                      <span className="ml-2 text-xs bg-green-100 text-green-800 px-2 py-0.5 rounded-full">
                        {entity.entity_group}
                      </span>
                    </div>
                  ))}
                </div>
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
