import { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [file, setFile] = useState(null);
  const [uploadStatus, setUploadStatus] = useState('');
  const [requirements, setRequirements] = useState(null);
  const [testResults, setTestResults] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [progress, setProgress] = useState('');

  const ALLOWED_TYPES = [
    'application/pdf',
    'application/vnd.openxmlformats-officedocument.wordprocessingml.document'
  ];

  // WebSocket Connection
  useEffect(() => {
    const ws = new WebSocket(`ws://${window.location.hostname}:5000`);

    ws.onmessage = (e) => {
      const data = JSON.parse(e.data);
      if (data.type === 'progress') {
        setProgress(data.message);
      }
    };

    return () => ws.close();
  }, []);

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    if (!selectedFile) return;

    setRequirements(null);
    setTestResults(null);
    setUploadStatus('');
    setProgress('');

    if (!ALLOWED_TYPES.includes(selectedFile.type)) {
      setUploadStatus('Error: Only PDF and DOCX files are allowed');
      return;
    }

    if (selectedFile.size > 5 * 1024 * 1024) {
      setUploadStatus('Error: File size must be less than 5MB');
      return;
    }

    setFile(selectedFile);
  };

  const handleUpload = async (e) => {
    e.preventDefault();
    if (!file) {
      setUploadStatus('Please select a valid file first');
      return;
    }

    const formData = new FormData();
    formData.append('srsDocument', file);

    try {
      setIsLoading(true);
      setUploadStatus('Uploading and analyzing document...');
      setProgress('Starting analysis...');

      // Verify backend health first
      await axios.get(`http://${window.location.hostname}:5000/api/health`, { timeout: 5000 });

      const res = await axios.post(`http://${window.location.hostname}:5000/api/upload`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        },
        timeout: 30000
      });

      setRequirements(res.data);
      setUploadStatus(
        res.data.analysisStatus === 'failed' 
          ? 'Analysis completed with warnings' 
          : 'Analysis complete!'
      );
      setProgress('');

    } catch (err) {
      let errorMsg = 'Processing failed. Please try again.';
      let details = '';

      if (err.code === 'ECONNABORTED') {
        errorMsg = 'Request timeout (30s) exceeded';
        details = 'Backend took too long to respond';
      } else if (err.response) {
        details = err.response.data.details || '';
        errorMsg = err.response.data.error || errorMsg;
      } else if (err.request) {
        details = 'Cannot connect to backend server';
      } else {
        details = err.message;
      }

      setUploadStatus(errorMsg);
      setTestResults({ error: errorMsg, details });
      setProgress('');
    } finally {
      setIsLoading(false);
    }
  };

  const runTests = async () => {
    if (!requirements?.testScript) return;

    try {
      setIsLoading(true);
      setUploadStatus('Running tests...');
      setTestResults(null);
      setProgress('Initializing test environment...');

      // Verify backend health first
      await axios.get(`http://${window.location.hostname}:5000/api/health`, { timeout: 5000 });

      const res = await axios.post(`http://${window.location.hostname}:5000/api/run-tests`, {
        testScript: requirements.testScript
      }, {
        timeout: 120000, // 2 minute timeout
        headers: { 'Content-Type': 'application/json' }
      });

      setTestResults(res.data);
      setUploadStatus(res.data.passed ? '✓ Tests passed!' : '✗ Tests failed');
      setProgress('');

      // Download report if tests completed
      if (res.data.passed !== undefined) {
        const reportRes = await axios.get(`http://${window.location.hostname}:5000/api/report/${Date.now()}`, {
          responseType: 'blob'
        });
        const url = window.URL.createObjectURL(new Blob([reportRes.data]));
        const link = document.createElement('a');
        link.href = url;
        link.setAttribute('download', `QA_Report_${new Date().toISOString()}.pdf`);
        document.body.appendChild(link);
        link.click();
      }

    } catch (err) {
      let errorMsg = 'Test execution failed';
      let details = '';

      if (err.code === 'ECONNABORTED') {
        errorMsg = 'Test timeout (120s) exceeded';
        details = 'Tests took too long to execute';
      } else if (err.response) {
        details = err.response.data.details || '';
        errorMsg = err.response.data.error || errorMsg;
      } else if (err.request) {
        details = 'Backend connection failed';
      } else {
        details = err.message;
      }

      setUploadStatus(errorMsg);
      setTestResults({ 
        error: errorMsg,
        details,
        logs: err.response?.data?.logs || ''
      });
      setProgress('');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 p-4 md:p-8">
      <div className="max-w-4xl mx-auto">
        <h1 className="text-2xl md:text-3xl font-bold text-blue-600 mb-6">
          QA Automation System
        </h1>

        {/* Upload Section */}
        <div className="bg-white p-4 md:p-6 rounded-lg shadow-md mb-6">
          <h2 className="text-lg md:text-xl font-semibold mb-4">
            Upload SRS Document
          </h2>

          <form onSubmit={handleUpload} className="space-y-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Select Document (PDF or DOCX, max 5MB)
              </label>
              <input
                type="file"
                onChange={handleFileChange}
                className="block w-full text-sm text-gray-500
                  file:mr-4 file:py-2 file:px-4
                  file:rounded-md file:border-0
                  file:text-sm file:font-semibold
                  file:bg-blue-50 file:text-blue-700
                  hover:file:bg-blue-100"
                accept=".pdf,.docx,application/pdf,application/vnd.openxmlformats-officedocument.wordprocessingml.document"
                disabled={isLoading}
              />
            </div>

            <button
              type="submit"
              className={`px-4 py-2 rounded-md text-white ${
                isLoading || !file ? 'bg-gray-400 cursor-not-allowed' : 'bg-blue-600 hover:bg-blue-700'
              }`}
              disabled={isLoading || !file}
            >
              {isLoading ? 'Processing...' : 'Analyze Document'}
            </button>

            {uploadStatus && (
              <p className={`mt-2 text-sm ${
                uploadStatus.startsWith('Error') || uploadStatus.startsWith('✗') ? 'text-red-600' :
                uploadStatus.includes('Processing') ? 'text-blue-600' :
                uploadStatus.includes('warning') ? 'text-yellow-600' :
                'text-green-600'
              }`}>
                {uploadStatus}
              </p>
            )}

            {progress && (
              <div className="mt-2">
                <p className="text-xs text-gray-600">Progress: {progress}</p>
                <div className="w-full bg-gray-200 rounded-full h-1.5 mt-1">
                  <div 
                    className="bg-blue-600 h-1.5 rounded-full" 
                    style={{ width: progress.includes('%') ? progress : '50%' }}
                  ></div>
                </div>
              </div>
            )}
          </form>
        </div>

        {/* Results Section */}
        {requirements && (
          <div className="space-y-6">
            {/* Requirements Analysis */}
            <div className="bg-white p-4 md:p-6 rounded-lg shadow-md">
              <h3 className="text-lg font-semibold mb-3">Requirements Analysis</h3>
              {requirements.requirements?.features?.labels?.length > 0 ? (
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  {requirements.requirements.features.labels.map((label, index) => (
                    <div key={index} className="border border-gray-200 p-3 rounded-lg">
                      <h4 className="font-medium capitalize">{label.replace(/-/g, ' ')}</h4>
                      <div className="w-full bg-gray-200 rounded-full h-2 mt-2">
                        <div 
                          className="bg-blue-600 h-2 rounded-full" 
                          style={{ 
                            width: `${Math.min(100, (requirements.requirements.features.scores[index] * 100).toFixed(0))}%` 
                          }}
                        ></div>
                      </div>
                      <p className="text-xs text-gray-600 mt-1">
                        Confidence: {(requirements.requirements.features.scores[index] * 100).toFixed(1)}%
                      </p>
                    </div>
                  ))}
                </div>
              ) : (
                <p className="text-yellow-600">No requirements could be extracted from the document</p>
              )}
            </div>

            {/* Generated Test Script */}
            <div className="bg-white p-4 md:p-6 rounded-lg shadow-md">
              <div className="flex justify-between items-center mb-3">
                <h3 className="text-lg font-semibold">Generated Test Script</h3>
                <div className="space-x-2">
                  <button
                    onClick={() => {
                      navigator.clipboard.writeText(requirements.testScript);
                      alert('Copied to clipboard!');
                    }}
                    className="px-3 py-1 bg-blue-600 text-white rounded hover:bg-blue-700 text-sm"
                  >
                    Copy
                  </button>
                  <button
                    onClick={runTests}
                    disabled={!requirements.testScript || isLoading}
                    className="px-3 py-1 bg-green-600 text-white rounded hover:bg-green-700 text-sm disabled:bg-gray-400"
                  >
                    {isLoading ? 'Running...' : 'Run Tests'}
                  </button>
                </div>
              </div>
              <div className="bg-gray-800 text-green-400 p-4 rounded overflow-auto max-h-96">
                <pre className="text-xs whitespace-pre-wrap">
                  {requirements.testScript}
                </pre>
              </div>
            </div>

            {/* Test Results */}
            {testResults && (
              <div className={`p-4 rounded-lg border ${
                testResults.passed ? 'bg-green-50 border-green-200' : 'bg-red-50 border-red-200'
              }`}>
                <div className="flex justify-between items-start">
                  <div>
                    <h3 className="font-semibold mb-2">
                      {testResults.passed ? '✓ Tests passed!' : '✗ Tests failed'}
                    </h3>
                    {testResults.error && (
                      <p className="text-red-600">{testResults.error}</p>
                    )}
                  </div>
                  {testResults.passed !== undefined && (
                    <button 
                      onClick={() => window.open(`http://${window.location.hostname}:5000/api/report/${Date.now()}`, '_blank')}
                      className="px-3 py-1 bg-blue-600 text-white rounded text-sm"
                    >
                      Download Full Report
                    </button>
                  )}
                </div>

                {testResults.logs && (
                  <div className="mt-3">
                    <details>
                      <summary className="cursor-pointer text-sm font-medium">View Execution Logs</summary>
                      <div className="bg-black text-white p-3 rounded overflow-auto max-h-40 mt-2">
                        <pre className="text-xs">{testResults.logs}</pre>
                      </div>
                    </details>
                  </div>
                )}
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
