import { useState } from 'react';
import axios from 'axios';

function App() {
  const [file, setFile] = useState(null);
  const [uploadStatus, setUploadStatus] = useState('');
  const [requirements, setRequirements] = useState(null);
  const [isLoading, setIsLoading] = useState(false);

  const ALLOWED_TYPES = [
    'application/pdf',
    'application/vnd.openxmlformats-officedocument.wordprocessingml.document'
  ];

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    if (!selectedFile) return;

    setRequirements(null);
    setUploadStatus('');

    if (!ALLOWED_TYPES.includes(selectedFile.type)) {
      setUploadStatus('Error: Only PDF and DOCX files are allowed');
      return;
    }

    if (selectedFile.size > 5 * 1024 * 1024) {
      setUploadStatus('Error: File size must be less than 5MB');
      return;
    }

    setFile(selectedFile);
  };

  const handleUpload = async (e) => {
    e.preventDefault();
    if (!file) {
      setUploadStatus('Please select a valid file first');
      return;
    }

    const formData = new FormData();
    formData.append('srsDocument', file);

    try {
      setIsLoading(true);
      setUploadStatus('Uploading and analyzing document...');

      const res = await axios.post('/api/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      setRequirements(res.data.requirements);
      setUploadStatus(
        res.data.requirements.analysisMethod === 'api' 
          ? 'Analysis complete!' 
          : 'Analysis completed (using local fallback)'
      );
    } catch (err) {
      setUploadStatus(
        err.response?.data?.error || 
        'Processing failed. Please try again.'
      );
      setFile(null);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 p-4 md:p-8">
      <div className="max-w-4xl mx-auto">
        <h1 className="text-2xl md:text-3xl font-bold text-blue-600 mb-6">QA Automation System</h1>

        <div className="bg-white p-4 md:p-6 rounded-lg shadow-md mb-6">
          <h2 className="text-lg md:text-xl font-semibold mb-4">Upload SRS Document</h2>

          <form onSubmit={handleUpload} className="space-y-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Select Document (PDF or DOCX, max 5MB)
              </label>
              <input
                type="file"
                onChange={handleFileChange}
                className="block w-full text-sm text-gray-500
                  file:mr-4 file:py-2 file:px-4
                  file:rounded-md file:border-0
                  file:text-sm file:font-semibold
                  file:bg-blue-50 file:text-blue-700
                  hover:file:bg-blue-100"
                accept=".pdf,.docx,application/pdf,application/vnd.openxmlformats-officedocument.wordprocessingml.document"
                disabled={isLoading}
              />
            </div>

            <button
              type="submit"
              className={`px-4 py-2 rounded-md text-white ${
                isLoading || !file ? 'bg-gray-400 cursor-not-allowed' : 'bg-blue-600 hover:bg-blue-700'
              }`}
              disabled={isLoading || !file}
            >
              {isLoading ? 'Processing...' : 'Analyze Document'}
            </button>

            {uploadStatus && (
              <p className={`mt-2 text-sm ${
                uploadStatus.startsWith('Error') ? 'text-red-600' :
                uploadStatus.includes('Processing') ? 'text-blue-600' :
                'text-green-600'
              }`}>
                {uploadStatus}
              </p>
            )}
          </form>
        </div>

        {requirements && (
          <div className="space-y-6">
            {requirements.analysisMethod === 'fallback' && (
              <div className="bg-yellow-50 p-4 rounded-lg border border-yellow-200">
                <p className="text-yellow-700">Basic analysis only. Some features may be limited.</p>
              </div>
            )}

            <div className="bg-white p-4 md:p-6 rounded-lg shadow-md">
              <h3 className="text-lg font-semibold mb-3">Document Content</h3>
              <div className="bg-gray-50 p-3 md:p-4 rounded max-h-60 overflow-auto">
                <pre className="text-sm whitespace-pre-wrap font-sans">
                  {requirements.rawText.substring(0, 1000)}
                  {requirements.rawText.length > 1000 && '...'}
                </pre>
                <p className="text-xs text-gray-500 mt-2">
                  Showing first {Math.min(requirements.rawText.length, 1000)} of {requirements.rawText.length} characters
                </p>
              </div>
            </div>

            {requirements.features && (
              <div className="bg-white p-4 md:p-6 rounded-lg shadow-md">
                <h3 className="text-lg font-semibold mb-3">Requirements Analysis</h3>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  {requirements.features.labels.map((label, index) => (
                    <div key={index} className="border border-gray-200 p-3 rounded-lg">
                      <h4 className="font-medium capitalize">{label.replace('-', ' ')}</h4>
                      <div className="w-full bg-gray-200 rounded-full h-2 mt-2">
                        <div 
                          className="bg-blue-600 h-2 rounded-full" 
                          style={{ width: `${(requirements.features.scores[index] * 100).toFixed(0)}%` }}
                        ></div>
                      </div>
                      <p className="text-xs text-gray-600 mt-1">
                        Confidence: {(requirements.features.scores[index] * 100).toFixed(1)}%
                      </p>
                    </div>
                  ))}
                </div>
              </div>
            )}

            {requirements.entities?.length > 0 && (
              <div className="bg-white p-4 md:p-6 rounded-lg shadow-md">
                <h3 className="text-lg font-semibold mb-3">Key Entities</h3>
                <div className="flex flex-wrap gap-2">
                  {requirements.entities.map((entity, index) => (
                    <div 
                      key={index} 
                      className="bg-green-50 px-3 py-1 rounded-full text-sm border border-green-100 flex items-center"
                    >
                      <span className="font-medium">{entity.word}</span>
                      <span className="ml-2 text-xs bg-green-100 text-green-800 px-2 py-0.5 rounded-full">
                        {entity.entity_group}
                      </span>
                    </div>
                  ))}
                </div>
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
