import { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [file, setFile] = useState(null);
  const [status, setStatus] = useState('');
  const [requirements, setRequirements] = useState(null);
  const [testResults, setTestResults] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [progress, setProgress] = useState('');

  // WebSocket Connection
  useEffect(() => {
    const ws = new WebSocket(`ws://${window.location.hostname}:5000`);

    ws.onmessage = (e) => {
      const data = JSON.parse(e.data);
      if (data.type === 'progress') {
        setProgress(data.message);
      }
    };

    return () => ws.close();
  }, []);

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    if (!selectedFile) return;

    setFile(selectedFile);
    setStatus('');
    setRequirements(null);
    setTestResults(null);
  };

  const handleUpload = async (e) => {
    e.preventDefault();
    if (!file) return;

    const formData = new FormData();
    formData.append('srsDocument', file);

    try {
      setIsLoading(true);
      setStatus('Analyzing document...');

      const res = await axios.post(
        `http://${window.location.hostname}:5000/api/upload`,
        formData,
        {
          headers: { 'Content-Type': 'multipart/form-data' },
          timeout: 30000
        }
      );

      setRequirements(res.data);
      setStatus('Analysis complete!');
    } catch (err) {
      setStatus(`Error: ${err.response?.data?.error || err.message}`);
    } finally {
      setIsLoading(false);
    }
  };

  const runTests = async () => {
    if (!requirements?.testScript) return;

    try {
      setIsLoading(true);
      setStatus('Running tests...');

      const res = await axios.post(
        `http://${window.location.hostname}:5000/api/run-tests`,
        { testScript: requirements.testScript },
        { timeout: 120000 }
      );

      setTestResults(res.data);
      setStatus(res.data.passed ? 'Tests passed!' : 'Tests failed');
    } catch (err) {
      setStatus(`Test error: ${err.response?.data?.error || err.message}`);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="app-container">
      <h1>QA Automation System</h1>

      <div className="upload-section">
        <input 
          type="file" 
          onChange={handleFileChange}
          accept=".pdf,.docx"
          disabled={isLoading}
        />
        <button 
          onClick={handleUpload}
          disabled={!file || isLoading}
        >
          {isLoading ? 'Processing...' : 'Analyze'}
        </button>
        {status && <p className={`status ${status.includes('Error') ? 'error' : ''}`}>{status}</p>}
        {progress && <p className="progress">Progress: {progress}</p>}
      </div>

      {requirements && (
        <div className="results">
          <h2>Requirements Analysis</h2>
          <ul>
            {requirements.labels.map((label, i) => (
              <li key={i}>
                {label}: {(requirements.scores[i] * 100).toFixed(1)}%
              </li>
            ))}
          </ul>

          <button 
            onClick={runTests}
            disabled={isLoading}
          >
            Run Tests
          </button>

          {testResults && (
            <div className="test-results">
              <h3>Test Results</h3>
              <pre>{testResults.logs}</pre>
            </div>
          )}
        </div>
      )}
    </div>
  );
}

export default App;
