import { useLocation } from 'react-router-dom';
import { useReactToPrint } from 'react-to-print';
import { useRef } from 'react';

function Report() {
  const { state } = useLocation();
  const reportRef = useRef();
  if (!state) {
    return (
      <div className="min-h-screen bg-gray-50 p-6 flex items-center justify-center">
        <div className="max-w-4xl mx-auto bg-white p-8 rounded-lg shadow-sm border border-gray-200 text-center">
          <h1 className="text-2xl font-bold text-red-600 mb-4">No Report Data Found</h1>
          <p className="text-gray-600">
            Please generate a report from the main application first.
          </p>
        </div>
      </div>
    );
  }

  // Fallback data if no state is passed
  
  const { requirements, testResults, documentName, timestamp } = state;
  const data = state || defaultData;

  
  return (
    <div className="min-h-screen bg-gray-50 p-6">
      {/* Report Content */}
      <div 
        ref={reportRef} 
        className="max-w-4xl mx-auto bg-white p-8 rounded-lg shadow-sm border border-gray-200"
      >
        {/* Header */}
        <div className="text-center mb-8">
          <h1 className="text-3xl font-bold text-blue-800">QA Test Report</h1>
          <div className="mt-4 text-gray-600 space-y-1">
            <p>Generated on: {new Date(data.timestamp).toLocaleString()}</p>
            <p>Document: <span className="font-medium">{data.documentName}</span></p>
          </div>
        </div>

        {/* Requirements Section */}
        <section className="mb-8">
          <h2 className="text-xl font-semibold border-b pb-2 mb-4">Requirements Analysis</h2>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            {data.requirements?.features?.labels?.map((label, index) => (
              <div key={index} className="border border-gray-200 p-4 rounded-lg">
                <h3 className="font-medium capitalize">{label.replace('-', ' ')}</h3>
                <div className="w-full bg-gray-200 rounded-full h-2.5 mt-2">
                  <div
                    className="bg-blue-600 h-2.5 rounded-full"
                    style={{
                      width: `${(data.requirements.features.scores[index] * 100).toFixed(0)}%`
                    }}
                  ></div>
                </div>
                <p className="text-sm text-gray-600 mt-1">
                  Confidence: {(data.requirements.features.scores[index] * 100).toFixed(1)}%
                </p>
              </div>
            ))}
          </div>
        </section>

        {/* Test Summary Section */}
        <section className="mb-8">
          <h2 className="text-xl font-semibold border-b pb-2 mb-4">Test Summary</h2>
          <div className="bg-blue-50 p-4 rounded-lg">
            <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
              <StatCard label="Total Tests" value={data.testResults?.summary?.total} />
              <StatCard 
                label="Passed" 
                value={data.testResults?.summary?.passed} 
                color="text-green-600" 
              />
              <StatCard 
                label="Failed" 
                value={data.testResults?.summary?.failed} 
                color="text-red-600" 
              />
              <StatCard label="Coverage" value={`${data.testResults?.summary?.coverage || "N/A"}%`} />
            </div>
          </div>
        </section>

        {/* Test Details Section */}
        <section>
          <h2 className="text-xl font-semibold border-b pb-2 mb-4">Test Details</h2>
          <div className="space-y-3">
            {data.testResults?.details?.map((test, index) => (
              <TestResultCard 
                key={index}
                title={test.title}
                status={test.status}
                duration={test.duration}
                error={test.error}
              />
            ))}
          </div>
        </section>

        {/* Footer */}
        <div className="mt-12 pt-4 border-t border-gray-200 text-center text-sm text-gray-500">
          <p>Generated by QA Automation System</p>
          <p className="mt-1">Confidential - For internal use only</p>
        </div>
      </div>

      {/* Print Button */}
      <div className="no-print mt-6 text-center">
        <button
          onClick={handlePrint}
          className="px-6 py-3 bg-blue-600 hover:bg-blue-700 text-white font-bold rounded-lg shadow-lg transition-colors"
        >
          Print Report
        </button>
      </div>
    </div>
  );
}

// Reusable Stat Card Component
function StatCard({ label, value, color = "" }) {
  return (
    <div>
      <p className="text-sm text-gray-600">{label}</p>
      <p className={`text-2xl font-bold ${color}`}>{value}</p>
    </div>
  );
}

// Reusable Test Result Card Component
function TestResultCard({ title, status, duration, error }) {
  return (
    <div className={`p-4 rounded-lg border ${
      status === "passed" 
        ? "bg-green-50 border-green-200" 
        : "bg-red-50 border-red-200"
    }`}>
      <div className="flex justify-between items-center">
        <h3 className="font-medium">{title}</h3>
        <span className={`px-2 py-1 rounded text-xs font-medium ${
          status === "passed" 
            ? "bg-green-100 text-green-800" 
            : "bg-red-100 text-red-800"
        }`}>
          {status.toUpperCase()}
        </span>
      </div>
      <p className="text-sm text-gray-600 mt-1">Duration: {duration}</p>
      {error && (
        <pre className="mt-2 p-2 bg-white text-red-500 text-xs rounded overflow-x-auto">
          Error: {error}
        </pre>
      )}
    </div>
  );
}

export default Report;
